<script>
	import { resolve } from '$app/paths';
	import { goto } from '$app/navigation';

	const resolved = resolve('/foo/');
	const empty = '';
	const keyValue = 'bar=baz';
	const queryValue = '?foo=bar';
	const section = 'section';
	const fragment = '#section';

	const query1 = resolve('/foo') + '?';
	const query2 = resolve('/foo') + '?foo=bar';
	const query3 = resolve('/foo') + '?' + 'foo=bar';
	const query4 = resolve('/foo') + '?' + keyValue;
	const query5 = resolve('/foo') + `?${keyValue}`;
	const query6 = resolve('/foo') + queryValue;
	const fragment1 = resolve('/foo') + '#';
	const fragment2 = resolve('/foo') + '#section';
	const fragment3 = resolve('/foo') + '#' + 'section';
	const fragment4 = resolve('/foo') + '#' + section;
	const fragment5 = resolve('/foo') + `#${section}`;
	const fragment6 = resolve('/foo') + fragment;

	goto(resolve('/foo') + '');
	goto(resolve('/foo') + empty);
	goto(resolve('/foo') + '?');
	goto(resolve('/foo') + '?foo=bar');
	goto(resolve('/foo') + '?' + 'foo=bar');
	goto(resolve('/foo') + '?' + keyValue);
	goto(resolve('/foo') + `?${keyValue}`);
	goto(resolve('/foo') + queryValue);
	goto(`${resolve('/foo')}?`);
	goto(`${resolve('/foo')}?foo=bar`);
	goto(`${resolve('/foo')}${'?' + 'foo=bar'}`);
	goto(`${resolve('/foo')}${'?' + keyValue}`);
	goto(`${resolve('/foo')}?${keyValue}`);
	goto(`${resolve('/foo')}${queryValue}`);
	goto(resolved + '?');
	goto(resolved + '?foo=bar');
	goto(resolved + '?' + 'foo=bar');
	goto(resolved + '?' + keyValue);
	goto(resolved + `?${keyValue}`);
	goto(resolved + queryValue);
	goto(`${resolved}?`);
	goto(`${resolved}?foo=bar`);
	goto(`${resolved}${'?' + 'foo=bar'}`);
	goto(`${resolved}${'?' + keyValue}`);
	goto(`${resolved}?${keyValue}`);
	goto(`${resolved}${queryValue}`);
	goto(query1);
	goto(query2);
	goto(query3);
	goto(query4);
	goto(query5);
	goto(query6);
	goto(resolve('/foo') + '#');
	goto(resolve('/foo') + '#section');
	goto(resolve('/foo') + '#' + 'section');
	goto(resolve('/foo') + '#' + section);
	goto(resolve('/foo') + `#${section}`);
	goto(resolve('/foo') + fragment);
	goto(`${resolve('/foo')}#`);
	goto(`${resolve('/foo')}#section`);
	goto(`${resolve('/foo')}${'#' + 'section'}`);
	goto(`${resolve('/foo')}${'#' + section}`);
	goto(`${resolve('/foo')}#${section}`);
	goto(`${resolve('/foo')}${fragment}`);
	goto(resolved + '#');
	goto(resolved + '#section');
	goto(resolved + '#' + 'section');
	goto(resolved + '#' + section);
	goto(resolved + `#${section}`);
	goto(resolved + fragment);
	goto(`${resolved}#`);
	goto(`${resolved}#section`);
	goto(`${resolved}${'#' + 'section'}`);
	goto(`${resolved}${'#' + section}`);
	goto(`${resolved}#${section}`);
	goto(`${resolved}${fragment}`);
	goto(fragment1);
	goto(fragment2);
	goto(fragment3);
	goto(fragment4);
	goto(fragment5);
	goto(fragment6);
</script>
