<script>
	import { resolve } from '$app/paths';
	import { pushState } from '$app/navigation';

	const resolved = resolve('/foo/');
	const empty = '';
	const keyValue = 'bar=baz';
	const queryValue = '?foo=bar';
	const section = 'section';
	const fragment = '#section';

	const query1 = resolve('/foo') + '?';
	const query2 = resolve('/foo') + '?foo=bar';
	const query3 = resolve('/foo') + '?' + 'foo=bar';
	const query4 = resolve('/foo') + '?' + keyValue;
	const query5 = resolve('/foo') + `?${keyValue}`;
	const query6 = resolve('/foo') + queryValue;
	const fragment1 = resolve('/foo') + '#';
	const fragment2 = resolve('/foo') + '#section';
	const fragment3 = resolve('/foo') + '#' + 'section';
	const fragment4 = resolve('/foo') + '#' + section;
	const fragment5 = resolve('/foo') + `#${section}`;
	const fragment6 = resolve('/foo') + fragment;

	pushState(resolve('/foo') + '');
	pushState(resolve('/foo') + empty);
	pushState(resolve('/foo') + '?');
	pushState(resolve('/foo') + '?foo=bar');
	pushState(resolve('/foo') + '?' + 'foo=bar');
	pushState(resolve('/foo') + '?' + keyValue);
	pushState(resolve('/foo') + `?${keyValue}`);
	pushState(resolve('/foo') + queryValue);
	pushState(`${resolve('/foo')}?`);
	pushState(`${resolve('/foo')}?foo=bar`);
	pushState(`${resolve('/foo')}${'?' + 'foo=bar'}`);
	pushState(`${resolve('/foo')}${'?' + keyValue}`);
	pushState(`${resolve('/foo')}?${keyValue}`);
	pushState(`${resolve('/foo')}${queryValue}`);
	pushState(resolved + '?');
	pushState(resolved + '?foo=bar');
	pushState(resolved + '?' + 'foo=bar');
	pushState(resolved + '?' + keyValue);
	pushState(resolved + `?${keyValue}`);
	pushState(resolved + queryValue);
	pushState(`${resolved}?`);
	pushState(`${resolved}?foo=bar`);
	pushState(`${resolved}${'?' + 'foo=bar'}`);
	pushState(`${resolved}${'?' + keyValue}`);
	pushState(`${resolved}?${keyValue}`);
	pushState(`${resolved}${queryValue}`);
	pushState(query1);
	pushState(query2);
	pushState(query3);
	pushState(query4);
	pushState(query5);
	pushState(query6);
	pushState(resolve('/foo') + '#');
	pushState(resolve('/foo') + '#section');
	pushState(resolve('/foo') + '#' + 'section');
	pushState(resolve('/foo') + '#' + section);
	pushState(resolve('/foo') + `#${section}`);
	pushState(resolve('/foo') + fragment);
	pushState(`${resolve('/foo')}#`);
	pushState(`${resolve('/foo')}#section`);
	pushState(`${resolve('/foo')}${'#' + 'section'}`);
	pushState(`${resolve('/foo')}${'#' + section}`);
	pushState(`${resolve('/foo')}#${section}`);
	pushState(`${resolve('/foo')}${fragment}`);
	pushState(resolved + '#');
	pushState(resolved + '#section');
	pushState(resolved + '#' + 'section');
	pushState(resolved + '#' + section);
	pushState(resolved + `#${section}`);
	pushState(resolved + fragment);
	pushState(`${resolved}#`);
	pushState(`${resolved}#section`);
	pushState(`${resolved}${'#' + 'section'}`);
	pushState(`${resolved}${'#' + section}`);
	pushState(`${resolved}#${section}`);
	pushState(`${resolved}${fragment}`);
	pushState(fragment1);
	pushState(fragment2);
	pushState(fragment3);
	pushState(fragment4);
	pushState(fragment5);
	pushState(fragment6);
</script>
